import React, { useState } from "react";

const TicketBooking = ({ movies, showtimes, bookings, setBookings }) => {
  const [newBooking, setNewBooking] = useState({ showtimeId: "", seats: [] });

  const handleBookSeats = () => {
    setBookings([...bookings, { ...newBooking, id: Date.now() }]);
    setNewBooking({ showtimeId: "", seats: [] });
  };

  return (
    <div>
      <h2>Ticket Booking</h2>
      <div>
        <select
          value={newBooking.showtimeId}
          onChange={(e) => setNewBooking({ ...newBooking, showtimeId: e.target.value })}
        >
          <option value="">Select Showtime</option>
          {showtimes.map((showtime) => (
            <option key={showtime.id} value={showtime.id}>
              {movies.find((movie) => movie.id === showtime.movieId)?.title || "Unknown Movie"} - {showtime.date} {showtime.time}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Seats (comma-separated)"
          value={newBooking.seats.join(",")}
          onChange={(e) =>
            setNewBooking({ ...newBooking, seats: e.target.value.split(",") })
          }
        />
        <button onClick={handleBookSeats}>Book Tickets</button>
      </div>
      <ul>
        {bookings.map((booking) => (
          <li key={booking.id}>
            Booking ID: {booking.id} - Showtime:{" "}
            {movies.find((movie) => movie.id === booking.showtimeId)?.title || "Unknown Movie"}
            - Seats: {booking.seats.join(", ")}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TicketBooking;
