import React, { useState } from "react";

const UserAccountManagement = ({ users, setUsers }) => {
  const [newUser, setNewUser] = useState({ username: "", email: "", password: "", role: "regular" });
  const [loginDetails, setLoginDetails] = useState({ username: "", password: "" });
  const [errorMessage, setErrorMessage] = useState("");
  const [loggedInUser, setLoggedInUser] = useState(null);

  const handleAddUser = () => {
    if (!newUser.username || !newUser.email || !newUser.password) {
      setErrorMessage("All fields are required for sign-up.");
      return;
    }

    // Validate email for regular users
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (newUser.role === "regular" && !emailRegex.test(newUser.email)) {
      setErrorMessage("Invalid email address.");
      return;
    }

    setUsers([...users, { ...newUser, id: Date.now() }]);
    setNewUser({ username: "", email: "", password: "", role: "regular" });
    setErrorMessage("");
  };

  const handleLogin = () => {
    const user = users.find(
      (user) => user.username === loginDetails.username && user.password === loginDetails.password
    );

    if (!user) {
      setErrorMessage("Invalid username or password.");
      return;
    }

    setLoggedInUser(user);
    setLoginDetails({ username: "", password: "" });
    setErrorMessage("");
  };

  const handleLogout = () => {
    setLoggedInUser(null);
  };

  return (
    <div>
      <h2>User Account Management</h2>

      {!loggedInUser ? (
        <>
          <div>
            <h3>Sign Up</h3>
            <input
              placeholder="Username"
              value={newUser.username}
              onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
            />
            <input
              placeholder="Email (for regular users)"
              value={newUser.email}
              onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
            />
            <input
              placeholder="Password"
              type="password"
              value={newUser.password}
              onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            />
            <select
              value={newUser.role}
              onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
            >
              <option value="regular">Regular User</option>
              <option value="admin">Admin</option>
            </select>
            <button onClick={handleAddUser}>Sign Up</button>
          </div>

          <div>
            <h3>Login</h3>
            <input
              placeholder="Username"
              value={loginDetails.username}
              onChange={(e) => setLoginDetails({ ...loginDetails, username: e.target.value })}
            />
            <input
              placeholder="Password"
              type="password"
              value={loginDetails.password}
              onChange={(e) => setLoginDetails({ ...loginDetails, password: e.target.value })}
            />
            <button onClick={handleLogin}>Login</button>
          </div>
          {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        </>
      ) : (
        <>
          <h3>Welcome, {loggedInUser.username}!</h3>
          <p>Role: {loggedInUser.role}</p>
          <button onClick={handleLogout}>Logout</button>
        </>
      )}

      <h3>Registered Users</h3>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            <strong>{user.username}</strong> ({user.email || "N/A"}) - Role: {user.role}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserAccountManagement;
